<?php require_once __DIR__ . '/../../includes/functions.php'; require_login(); $id=(int)($_POST['id']??0); $body=trim($_POST['body']??''); if(!$id||!$body){ http_response_code(400); exit; } $pdo->prepare('INSERT INTO messages(ticket_id,sender_id,body,created_at) VALUES(?,?,?,NOW())')->execute([$id,current_user()['id'],$body]); $mid=$pdo->lastInsertId(); $m=$pdo->prepare('SELECT m.*, u.name FROM messages m LEFT JOIN users u ON u.id=m.sender_id WHERE m.id=?'); $m->execute([$mid]); $row=$m->fetch(); echo '<div class="d-flex gap-2 my-1"><span class="avatar"></span><div><div><b>'.e($row['name']).'</b> <span class="small">'.e($row['created_at']).'</span></div><div>'.nl2br(e($row['body'])).'</div></div></div>';